package Day4Pattern;

public class Pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<=3;i++)
		{
			for(int j=0;j<=3;j++)
			{
				if(j>=1 && j<=2)
				{
					System.out.print("  ");
				}
				else
				{
					System.out.print("*");
				}
			}
			System.out.println();
		}






	}

}
