package Day4Pattern;

public class Plus {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++)
		{
			if(i==3)
			{
				for(int j=1;j<=5;j++)
				{
					System.out.print("*");
					}
			}
				else
				{
					for(int k=1;k<=2;k++)
					{
						System.out.print(" ");
					}
					System.out.print("*");
				}
				System.out.println();
				}
			}
			
				
			
		}

	


